word1 = "abc"
word2 = "abd"
from functools import lru_cache
class Solution:
    def largestMerge(self, word1: str, word2: str) -> str:
        m, n = len(word1), len(word2)
        i = j = 0
        res = ""
        while i < m and j < n:
            if word1[i] > word2[j]:
                res += word1[i]
                i += 1
            elif word1[i] < word2[j]:
                res += word2[j]
                j += 1
            else:
                tmpi, tmpj = i + 1, j + 1
                
                while tmpi < m and tmpj < n and word1[tmpi] == word2[tmpj] and word1[tmpi] < word1[i]:
                    tmpi += 1
                    tmpj += 1
                if tmpi < m and tmpj < n:
                    if word1[tmpi] != word2[tmpj]:
                        if word1[tmpi] > word2[tmpj]:
                            res += word1[i:tmpi]
                            i = tmpi
                        else:
                            res += word2[j:tmpj]
                            j = tmpj
                    else:
                        res += word1[i:tmpi]
                        i = tmpi
                if tmpi == m:
                    res += word2[j:tmpj]
                    j = tmpj
                elif tmpj == n:
                    res += word1[i:tmpi]
                    i = tmpi
        while i < m:
            res += word1[i]
            i += 1
        while j < n:
            res += word2[j]
            j += 1
        return res

sol = Solution()
print(sol.largestMerge(word1, word2))
            
        